import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { NavbarComponent } from '../../../ui/navbar/navbar.component';
import { ActivityService } from '../../../core/services/activity.service';
import { AuthService } from '../../../core/services/auth.service';
import { User } from '../../../core/models/user.model';
import { Activity } from '../../../core/models/activity.model';

interface StudentReport {
  studentId: string;
  studentName: string;
  totalPoints: number;
  activities: Activity[];
}

@Component({
  selector: 'app-teacher-dashboard',
  standalone: true,
  imports: [CommonModule, RouterModule, NavbarComponent],
  template: `
    <app-navbar></app-navbar>
    <div class="container mx-auto px-4 py-8">
      <div class="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <!-- Stats Cards -->
        <div class="bg-white rounded-lg shadow p-6">
          <h3 class="text-lg font-semibold mb-2">Pending Reviews</h3>
          <p class="text-3xl font-bold text-blue-600">{{ stats.pending }}</p>
        </div>
        <div class="bg-white rounded-lg shadow p-6">
          <h3 class="text-lg font-semibold mb-2">Approved Activities</h3>
          <p class="text-3xl font-bold text-green-600">{{ stats.approved }}</p>
        </div>
        <div class="bg-white rounded-lg shadow p-6">
          <h3 class="text-lg font-semibold mb-2">Rejected Activities</h3>
          <p class="text-3xl font-bold text-red-600">{{ stats.rejected }}</p>
        </div>
      </div>

      <!-- Pending Activities -->
      <div class="bg-white rounded-lg shadow p-6 mb-8">
        <h2 class="text-xl font-bold mb-4">Pending Activities</h2>
        <div class="overflow-x-auto">
          <table class="min-w-full">
            <thead>
              <tr class="bg-gray-100">
                <th class="px-4 py-2">Student</th>
                <th class="px-4 py-2">Activity</th>
                <th class="px-4 py-2">Points</th>
                <th class="px-4 py-2">Date</th>
                <th class="px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let activity of pendingActivities">
                <td class="px-4 py-2">{{ activity.studentName }}</td>
                <td class="px-4 py-2">{{ activity.title }}</td>
                <td class="px-4 py-2">{{ activity.points }}</td>
                <td class="px-4 py-2">{{ activity.date | date }}</td>
                <td class="px-4 py-2">
                  <button (click)="reviewActivity(activity._id, 'approved')" 
                          class="bg-green-500 text-white px-3 py-1 rounded mr-2">
                    Approve
                  </button>
                  <button (click)="reviewActivity(activity._id, 'rejected')"
                          class="bg-red-500 text-white px-3 py-1 rounded">
                    Reject
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>

      <!-- Student Reports -->
      <div class="bg-white rounded-lg shadow p-6">
        <h2 class="text-xl font-bold mb-4">Student Reports</h2>
        <div class="overflow-x-auto">
          <table class="min-w-full">
            <thead>
              <tr class="bg-gray-100">
                <th class="px-4 py-2">Student Name</th>
                <th class="px-4 py-2">Roll Number</th>
                <th class="px-4 py-2">Total Points</th>
                <th class="px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let report of studentReports">
                <td class="px-4 py-2">{{ report.studentName }}</td>
                <td class="px-4 py-2">{{ report.activities[0]?.studentRollNumber }}</td>
                <td class="px-4 py-2">{{ report.totalPoints }}</td>
                <td class="px-4 py-2">
                  <button (click)="viewStudentDetails(report.studentId)"
                          class="bg-blue-500 text-white px-3 py-1 rounded">
                    View Details
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  `,
  styles: [`
    :host {
      display: block;
      min-height: 100vh;
      background-color: #f3f4f6;
    }
  `]
})
export class TeacherDashboardComponent implements OnInit {
  currentUser: User | null = null;
  pendingActivities: Activity[] = [];
  studentReports: StudentReport[] = [];
  stats = {
    pending: 0,
    approved: 0,
    rejected: 0
  };

  constructor(
    private authService: AuthService,
    private activityService: ActivityService
  ) {}

  ngOnInit(): void {
    this.currentUser = this.authService.getCurrentUser();
    if (this.currentUser) {
      this.loadPendingActivities();
      this.loadTeacherReport();
    }
  }

  loadPendingActivities(): void {
    this.activityService.getPendingActivities().subscribe({
      next: (response) => {
        this.pendingActivities = response.data;
        this.stats = response.stats;
      },
      error: (error) => {
        console.error('Error loading pending activities:', error);
      }
    });
  }

  loadTeacherReport(): void {
    if (this.currentUser?._id) {
      this.activityService.getTeacherReport(this.currentUser._id).subscribe({
        next: (response) => {
          this.studentReports = response.data;
        },
        error: (error) => {
          console.error('Error loading teacher report:', error);
        }
      });
    }
  }

  reviewActivity(activityId: string, status: 'approved' | 'rejected'): void {
    const reviewData = {
      status,
      feedback: status === 'approved' ? 'Activity approved' : 'Activity rejected'
    };

    this.activityService.reviewActivity(activityId, reviewData).subscribe({
      next: () => {
        this.loadPendingActivities();
        this.loadTeacherReport();
      },
      error: (error) => {
        console.error('Error reviewing activity:', error);
      }
    });
  }

  viewStudentDetails(studentId: string): void {
    // Navigate to detailed student report view
    // This would be implemented in a separate component
  }
}
