import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router, RouterLink } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { NavbarComponent } from '../../../ui/navbar/navbar.component';
import { ActivityService } from '../../../core/services/activity.service';
import { AuthService } from '../../../core/services/auth.service';
import { User } from '../../../core/models/user.model';

@Component({
  selector: 'app-activity-submit',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, RouterLink, NavbarComponent],
  templateUrl: './activity-submit.component.html',
  styleUrls: ['./activity-submit.component.scss']
})
export class ActivitySubmitComponent implements OnInit {
  activityForm: FormGroup;
  currentUser: User | null = null;
  isLoading = false;
  isSubmitting = false;
  errorMessage = '';
  successMessage = '';
  selectedFile: File | null = null;
  calculatedPoints = 0;

  activityTypes = ['sports', 'mooc', 'workshops', 'internships'];
  levels = [1, 2, 3, 4, 5];
  semesters = [1, 2, 3, 4, 5, 6, 7, 8];

  private sportsPointsMap: Record<number, number> = {
    1: 8,
    2: 15,
    3: 25,
    4: 40,
    5: 50
  };

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private activityService: ActivityService,
    private router: Router,
    private http: HttpClient
  ) {
    this.activityForm = this.fb.group({
      title: ['', [Validators.required, Validators.minLength(3)]],
      activityType: ['', Validators.required],
      description: ['', [Validators.required, Validators.minLength(10)]],
      eventOrganizer: ['', [Validators.required, Validators.minLength(3)]],
      date: ['', Validators.required],
      level: [null],
      points: [0],
      semester: ['', [Validators.required, Validators.min(1), Validators.max(8)]]
    });

    // Calculate points when activity type or level changes
    this.activityForm.get('activityType')?.valueChanges.subscribe(() => {
      this.calculatePoints();
      this.updateLevelValidation();
    });

    this.activityForm.get('level')?.valueChanges.subscribe(() => {
      this.calculatePoints();
    });

    // Mark form as touched when any field changes
    Object.keys(this.activityForm.controls).forEach(key => {
      this.activityForm.get(key)?.valueChanges.subscribe(() => {
        this.activityForm.get(key)?.markAsTouched();
      });
    });
  }

  ngOnInit(): void {
    this.currentUser = this.authService.getCurrentUser();
    if (!this.currentUser) {
      this.router.navigate(['/auth/login']);
    }
  }

  updateLevelValidation(): void {
    const activityType = this.activityForm.get('activityType')?.value;
    const levelControl = this.activityForm.get('level');

    if (activityType === 'sports') {
      levelControl?.setValidators([Validators.required]);
    } else {
      levelControl?.clearValidators();
      levelControl?.setValue(null);
    }
    levelControl?.updateValueAndValidity();
  }

  calculatePoints(): void {
    const activityType = this.activityForm.get('activityType')?.value;
    const level = Number(this.activityForm.get('level')?.value);

    if (!activityType) {
      this.calculatedPoints = 0;
      return;
    }

    switch (activityType) {
      case 'sports':
        this.calculatedPoints = this.sportsPointsMap[level] || 0;
        break;
      case 'mooc':
        this.calculatedPoints = 50;
        break;
      case 'workshops':
        this.calculatedPoints = 6;
        break;
      case 'internships':
        this.calculatedPoints = 20;
        break;
      default:
        this.calculatedPoints = 0;
    }

    this.activityForm.patchValue({ points: this.calculatedPoints });
  }

  onFileSelected(event: any): void {
    const file = event.target.files[0];
    if (file) {
      // Check file size (5MB limit)
      if (file.size > 5 * 1024 * 1024) {
        this.errorMessage = 'File size exceeds 5MB limit';
        event.target.value = '';
        this.selectedFile = null;
        return;
      }

      // Check file type
      const allowedTypes = ['application/pdf', 'image/jpeg', 'image/jpg', 'image/png'];
      if (!allowedTypes.includes(file.type)) {
        this.errorMessage = 'Invalid file type. Please upload PDF, JPG, JPEG, or PNG files only';
        event.target.value = '';
        this.selectedFile = null;
        return;
      }

      this.selectedFile = file;
      this.errorMessage = '';
    }
  }

  onSubmit(): void {
    // Mark all fields as touched to trigger validation
    Object.keys(this.activityForm.controls).forEach(key => {
      this.activityForm.get(key)?.markAsTouched();
    });

    // Log form state for debugging
    console.log('Form state:', {
      valid: this.activityForm.valid,
      values: this.activityForm.value,
      errors: this.activityForm.errors,
      selectedFile: this.selectedFile,
      calculatedPoints: this.calculatedPoints,
      currentUser: this.currentUser ? {
        id: this.currentUser._id,
        name: this.currentUser.name
      } : null
    });

    if (!this.currentUser) {
      console.error('No user logged in');
      this.errorMessage = 'Please login to submit an activity.';
      this.router.navigate(['/auth/login']);
      return;
    }

    if (!this.selectedFile) {
      console.error('No file selected');
      this.errorMessage = 'Please upload a certificate.';
      return;
    }

    if (this.activityForm.invalid) {
      console.error('Form validation failed:', this.activityForm.errors);
      this.errorMessage = 'Please fill in all required fields correctly.';
      return;
    }

    this.isSubmitting = true;
    this.errorMessage = '';
    this.successMessage = '';

    try {
      const formData = new FormData();
      const formValues = this.activityForm.value;

      // Log the data being sent
      console.log('Preparing to send data:', {
        title: formValues.title,
        activityType: formValues.activityType,
        description: formValues.description,
        eventOrganizer: formValues.eventOrganizer,
        date: formValues.date,
        semester: formValues.semester,
        points: this.calculatedPoints,
        level: formValues.level,
        studentId: this.currentUser._id,
        fileSize: this.selectedFile.size,
        fileType: this.selectedFile.type
      });

      // Add the certificate file
      formData.append('certificateFile', this.selectedFile);

      // Add basic activity details
      formData.append('title', formValues.title);
      formData.append('activityType', formValues.activityType);
      formData.append('description', formValues.description);
      formData.append('eventOrganizer', formValues.eventOrganizer);
      formData.append('date', formValues.date);
      formData.append('semester', formValues.semester.toString());
      formData.append('points', this.calculatedPoints.toString());

      // Add level for sports activities
      if (formValues.activityType === 'sports' && formValues.level) {
        formData.append('level', formValues.level.toString());
      }

      // Add student details
      formData.append('studentId', this.currentUser._id);
      formData.append('studentName', this.currentUser.name);
      formData.append('studentDepartment', this.currentUser.department || '');
      formData.append('studentClass', this.currentUser.class || '');
      formData.append('studentSemester', (this.currentUser.semester || '').toString());
      formData.append('studentRollNumber', this.currentUser.rollNumber || '');

      console.log('Submitting activity...');

      this.activityService.submitActivity(formData).subscribe({
        next: (response) => {
          console.log('Submission successful:', response);
          this.isSubmitting = false;
          this.successMessage = 'Activity submitted successfully!';

          // Reset form
          this.activityForm.reset();
          this.selectedFile = null;
          const fileInput = document.getElementById('certificateFile') as HTMLInputElement;
          if (fileInput) {
            fileInput.value = '';
          }

          setTimeout(() => {
            this.router.navigate(['/dashboard/student']);
          }, 2000);
        },
        error: (error) => {
          console.error('Submit error details:', {
            message: error.message,
            error: error
          });
          this.isSubmitting = false;

          // Handle specific error cases
          if (error.message.includes('Session expired')) {
            this.errorMessage = 'Your session has expired. Please login again.';
            this.router.navigate(['/auth/login']);
            return;
          }

          if (error.message.includes('File size exceeds')) {
            this.errorMessage = 'The file is too large. Please upload a file smaller than 5MB.';
            return;
          }

          if (error.message.includes('Invalid form data')) {
            this.errorMessage = 'Please check all your inputs and try again.';
            return;
          }

          this.errorMessage = error.message || 'Failed to submit activity. Please try again.';
        }
      });
    } catch (error) {
      console.error('Error preparing form data:', error);
      this.isSubmitting = false;
      this.errorMessage = 'An error occurred while preparing your submission.';
    }
  }
}
