import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, ReactiveFormsModule } from '@angular/forms';
import { ActivityService } from '../../../../core/services/activity.service';
import { Activity } from '../../../../core/models/activity.model';

interface ApiResponse<T> {
  success: boolean;
  count?: number;
  data: T;
  message?: string;
}

@Component({
  selector: 'app-report',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  template: `
    <div class="container mx-auto px-4 py-8">
      <div class="bg-white rounded-lg shadow p-6">
        <h2 class="text-xl font-bold mb-4">Generate Report</h2>
        
        <form [formGroup]="reportForm" (ngSubmit)="onSubmit()" class="space-y-4">
          <div class="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label class="block text-sm font-medium text-gray-700">Department</label>
              <input type="text" formControlName="department" 
                     class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500">
            </div>
            
            <div>
              <label class="block text-sm font-medium text-gray-700">Class</label>
              <input type="text" formControlName="class" 
                     class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500">
            </div>
            
            <div>
              <label class="block text-sm font-medium text-gray-700">Semester</label>
              <select formControlName="semester" 
                      class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500">
                <option value="">Select Semester</option>
                <option *ngFor="let sem of semesters" [value]="sem">{{ sem }}</option>
              </select>
            </div>
            
            <div>
              <label class="block text-sm font-medium text-gray-700">Status</label>
              <select formControlName="status" 
                      class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500">
                <option value="">All</option>
                <option value="pending">Pending</option>
                <option value="approved">Approved</option>
                <option value="rejected">Rejected</option>
              </select>
            </div>
          </div>
          
          <div class="flex justify-end">
            <button type="submit" 
                    [disabled]="reportForm.invalid || isLoading"
                    class="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 disabled:opacity-50">
              {{ isLoading ? 'Generating...' : 'Generate Report' }}
            </button>
          </div>
        </form>

        <div *ngIf="errorMessage" class="mt-4 p-4 bg-red-100 text-red-700 rounded">
          {{ errorMessage }}
        </div>

        <div *ngIf="activities.length > 0" class="mt-8">
          <h3 class="text-lg font-semibold mb-4">Report Results</h3>
          <div class="overflow-x-auto">
            <table class="min-w-full">
              <thead>
                <tr class="bg-gray-100">
                  <th class="px-4 py-2">Student</th>
                  <th class="px-4 py-2">Activity</th>
                  <th class="px-4 py-2">Points</th>
                  <th class="px-4 py-2">Status</th>
                  <th class="px-4 py-2">Date</th>
                </tr>
              </thead>
              <tbody>
                <tr *ngFor="let activity of activities">
                  <td class="px-4 py-2">{{ activity.studentName }}</td>
                  <td class="px-4 py-2">{{ activity.title }}</td>
                  <td class="px-4 py-2">{{ activity.pointsAwarded || activity.points }}</td>
                  <td class="px-4 py-2">
                    <span [class]="getStatusClass(activity.status)">
                      {{ activity.status }}
                    </span>
                  </td>
                  <td class="px-4 py-2">{{ activity.date | date }}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    :host {
      display: block;
    }
  `]
})
export class ReportComponent implements OnInit {
  reportForm: FormGroup;
  activities: Activity[] = [];
  isLoading = false;
  errorMessage = '';
  semesters = [1, 2, 3, 4, 5, 6, 7, 8];

  constructor(
    private fb: FormBuilder,
    private activityService: ActivityService
  ) {
    this.reportForm = this.fb.group({
      department: [''],
      class: [''],
      semester: [''],
      status: ['']
    });
  }

  ngOnInit(): void {}

  onSubmit(): void {
    if (this.reportForm.invalid) {
      return;
    }

    this.isLoading = true;
    this.errorMessage = '';
    this.activities = [];

    const formValue = this.reportForm.value;
    const params = {
      department: formValue.department || undefined,
      class: formValue.class || undefined,
      semester: formValue.semester ? parseInt(formValue.semester) : undefined,
      status: formValue.status || undefined
    };

    this.activityService.generateReport(params).subscribe({
      next: (response: ApiResponse<Activity[]>) => {
        if (response.success) {
          this.activities = response.data;
        } else {
          this.errorMessage = response.message || 'Failed to generate report';
        }
        this.isLoading = false;
      },
      error: (error: Error) => {
        this.errorMessage = error.message;
        this.isLoading = false;
      }
    });
  }

  getStatusClass(status: string): string {
    switch (status) {
      case 'approved':
        return 'text-green-600 font-semibold';
      case 'rejected':
        return 'text-red-600 font-semibold';
      case 'pending':
        return 'text-yellow-600 font-semibold';
      default:
        return '';
    }
  }
}
