import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router, RouterLink } from '@angular/router';
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { NavbarComponent } from '../../../ui/navbar/navbar.component';
import { ActivityService } from '../../../core/services/activity.service';
import { AuthService } from '../../../core/services/auth.service';
import { User } from '../../../core/models/user.model';
import { Activity } from '../../../core/models/activity.model';
import { ApiResponse } from '../../../core/models/api-response.model';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-teacher-dashboard',
  standalone: true,
  imports: [CommonModule, RouterLink, ReactiveFormsModule, NavbarComponent],
  templateUrl: './teacher-dashboard.component.html',
  styleUrls: ['./teacher-dashboard.component.scss']
})
export class TeacherDashboardComponent implements OnInit {
  currentUser: User | null = null;
  activities: Activity[] = [];
  pendingActivities: Activity[] = [];
  isLoading = true;
  errorMessage = '';
  selectedActivity: Activity | null = null;
  pendingCount = 0;
  approvedCount = 0;
  rejectedCount = 0;
  reviewForm: FormGroup;

  constructor(
    private activityService: ActivityService,
    private authService: AuthService,
    private router: Router,
    private fb: FormBuilder
  ) {
    this.reviewForm = this.fb.group({
      status: ['approved', Validators.required],
      feedback: ['']
    });
  }

  ngOnInit(): void {
    this.currentUser = this.authService.getCurrentUser();
    if (!this.currentUser || this.currentUser.role !== 'teacher') {
      this.router.navigate(['/auth/login']);
      return;
    }
    this.loadActivities();
  }

  private loadActivities(): void {
    this.isLoading = true;
    this.errorMessage = '';

    this.activityService.getAllActivities().subscribe({
      next: (response: ApiResponse<Activity[]>) => {
        if (response.success && response.data) {
          this.activities = response.data;
          this.pendingActivities = this.activities.filter(a => a.status === 'pending');
          this.calculateStats();
        } else {
          this.errorMessage = response.message || 'Failed to load activities';
        }
        this.isLoading = false;
      },
      error: (error: HttpErrorResponse) => {
        console.error('Error loading activities:', error);
        this.errorMessage = 'Failed to load activities. Please try again later.';
        this.isLoading = false;
      }
    });
  }

  private calculateStats(): void {
    this.pendingCount = this.activities.filter(a => a.status === 'pending').length;
    this.approvedCount = this.activities.filter(a => a.status === 'approved').length;
    this.rejectedCount = this.activities.filter(a => a.status === 'rejected').length;
  }

  selectActivity(activity: Activity): void {
    this.selectedActivity = activity;
    this.reviewForm.patchValue({
      status: 'approved',
      feedback: ''
    });
  }

  closeModal(): void {
    this.selectedActivity = null;
  }

  approveActivity(activityId: string): void {
    this.activityService.updateActivityStatus(activityId, 'approved').subscribe({
      next: (response: ApiResponse<Activity>) => {
        if (response.success) {
          this.loadActivities();
          this.closeModal();
        } else {
          this.errorMessage = response.message || 'Failed to approve activity';
        }
      },
      error: (error: HttpErrorResponse) => {
        console.error('Error approving activity:', error);
        this.errorMessage = 'Failed to approve activity. Please try again.';
      }
    });
  }

  rejectActivity(activityId: string): void {
    this.activityService.updateActivityStatus(activityId, 'rejected').subscribe({
      next: (response: ApiResponse<Activity>) => {
        if (response.success) {
          this.loadActivities();
          this.closeModal();
        } else {
          this.errorMessage = response.message || 'Failed to reject activity';
        }
      },
      error: (error: HttpErrorResponse) => {
        console.error('Error rejecting activity:', error);
        this.errorMessage = 'Failed to reject activity. Please try again.';
      }
    });
  }

  submitReview(): void {
    if (!this.selectedActivity || !this.reviewForm.valid) {
      return;
    }

    const status = this.reviewForm.get('status')?.value;
    const feedback = this.reviewForm.get('feedback')?.value;

    if (status === 'approved') {
      this.activityService.reviewActivity(this.selectedActivity._id, {
        status: 'approved',
        feedback: feedback || undefined
      }).subscribe({
        next: (response: ApiResponse<Activity>) => {
          if (response.success) {
            this.loadActivities();
            this.closeModal();
          } else {
            this.errorMessage = response.message || 'Failed to approve activity';
          }
        },
        error: (error: HttpErrorResponse) => {
          console.error('Error approving activity:', error);
          this.errorMessage = error.error?.message || 'Failed to approve activity. Please try again.';
        }
      });
    } else {
      this.activityService.reviewActivity(this.selectedActivity._id, {
        status: 'rejected',
        feedback: feedback || undefined
      }).subscribe({
        next: (response: ApiResponse<Activity>) => {
          if (response.success) {
            this.loadActivities();
            this.closeModal();
          } else {
            this.errorMessage = response.message || 'Failed to reject activity';
          }
        },
        error: (error: HttpErrorResponse) => {
          console.error('Error rejecting activity:', error);
          this.errorMessage = error.error?.message || 'Failed to reject activity. Please try again.';
        }
      });
    }
  }

  isUserObject(value: any): value is User {
    return value && typeof value === 'object' && 'role' in value;
  }

  getCertificateUrl(certificateFile: string | undefined): string {
    if (!certificateFile) return '';
    return `${this.activityService.getApiUrl()}/uploads/${certificateFile}`;
  }
}
