import { Injectable } from '@angular/core';
import { HttpClient, HttpErrorResponse, HttpHeaders } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError, map } from 'rxjs/operators';
import { Activity } from '../models/activity.model';
import { environment } from '../../../environments/environment';
import { AuthService } from './auth.service';

// Define the API response interfaces
interface ApiResponse<T> {
  success: boolean;
  count?: number;
  data: T;
  message?: string;
}

interface PendingActivitiesResponse {
  success: boolean;
  count: number;
  stats: {
    pending: number;
    approved: number;
    rejected: number;
  };
  data: Activity[];
}

interface StudentReport {
  studentId: string;
  studentName: string;
  totalPoints: number;
  activities: Activity[];
}

interface ReportParams {
  teacherId?: string;
  studentIds?: string[];
  department?: string;
  class?: string;
  semester?: number;
  status?: string;
}

@Injectable({
  providedIn: 'root'
})
export class ActivityService {
  private apiUrl = `${environment.apiUrl}/api/activities`;

  constructor(
    private http: HttpClient,
    private authService: AuthService
  ) { }

  private getHeaders(): HttpHeaders {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('No authentication token found');
    }
    return new HttpHeaders({
      'Authorization': `Bearer ${token}`
    });
  }

  submitActivity(formData: FormData): Observable<ApiResponse<Activity>> {
    const headers = this.getHeaders();
    // Remove Content-Type header to let browser set it with boundary for FormData
    headers.delete('Content-Type');

    return this.http.post<ApiResponse<Activity>>(`${this.apiUrl}`, formData, { 
      headers,
      reportProgress: true
    }).pipe(
      catchError(this.handleError)
    );
  }

  getMyActivities(): Observable<ApiResponse<Activity[]>> {
    const headers = this.getHeaders();
    return this.http.get<ApiResponse<Activity[]>>(`${this.apiUrl}/my-activities`, { headers })
      .pipe(catchError(this.handleError));
  }

  getPendingActivities(): Observable<PendingActivitiesResponse> {
    const headers = this.getHeaders();
    return this.http.get<PendingActivitiesResponse>(`${this.apiUrl}/pending`, { headers })
      .pipe(catchError(this.handleError));
  }

  reviewActivity(id: string, reviewData: { 
    status: 'approved' | 'rejected', 
    feedback?: string, 
    pointsAwarded?: number 
  }): Observable<ApiResponse<Activity>> {
    const headers = this.getHeaders();
    return this.http.put<ApiResponse<Activity>>(
      `${this.apiUrl}/${id}/review`, 
      reviewData,
      { headers }
    ).pipe(catchError(this.handleError));
  }

  getStudentReport(studentId: string): Observable<ApiResponse<StudentReport>> {
    const headers = this.getHeaders();
    return this.http.get<ApiResponse<StudentReport>>(
      `${this.apiUrl}/student-report/${studentId}`,
      { headers }
    ).pipe(catchError(this.handleError));
  }

  getTeacherReport(teacherId: string): Observable<ApiResponse<StudentReport[]>> {
    const headers = this.getHeaders();
    return this.http.get<ApiResponse<StudentReport[]>>(
      `${this.apiUrl}/teacher-report/${teacherId}`,
      { headers }
    ).pipe(catchError(this.handleError));
  }

  generateReport(params: ReportParams): Observable<ApiResponse<Activity[]>> {
    const headers = this.getHeaders();
    const queryParams = new URLSearchParams();

    if (params.teacherId) queryParams.append('teacherId', params.teacherId);
    if (params.studentIds?.length) queryParams.append('studentIds', params.studentIds.join(','));
    if (params.department) queryParams.append('department', params.department);
    if (params.class) queryParams.append('class', params.class);
    if (params.semester) queryParams.append('semester', params.semester.toString());
    if (params.status) queryParams.append('status', params.status);

    const url = `${this.apiUrl}/report?${queryParams.toString()}`;
    return this.http.get<ApiResponse<Activity[]>>(url, { headers })
      .pipe(catchError(this.handleError));
  }

  private handleError(error: HttpErrorResponse): Observable<never> {
    let errorMessage = 'An unknown error occurred';

    if (error.error instanceof ErrorEvent) {
      errorMessage = `Error: ${error.error.message}`;
    } else {
      switch (error.status) {
        case 401:
          errorMessage = 'Session expired. Please login again.';
          this.authService.logout();
          break;
        case 403:
          errorMessage = 'You do not have permission to perform this action.';
          break;
        case 404:
          errorMessage = 'The requested resource was not found. Please check the API endpoint.';
          console.error('API Endpoint:', error.url);
          break;
        case 413:
          errorMessage = 'File size too large. Please upload a smaller file.';
          break;
        case 415:
          errorMessage = 'Invalid file type. Please upload PDF, JPG, JPEG, or PNG files only.';
          break;
        case 500:
          errorMessage = 'Server error. Please try again later.';
          break;
        default:
          errorMessage = error.error?.message || 'An error occurred. Please try again.';
      }
    }

    console.error('API Error:', error);
    return throwError(() => new Error(errorMessage));
  }
}
