import { Component, OnInit, ViewChild, ElementRef, AfterViewInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router, RouterLink } from '@angular/router';
import { Chart, ChartConfiguration, ChartType } from 'chart.js';
import { NavbarComponent } from '../../../ui/navbar/navbar.component';
import { ActivityService } from '../../../core/services/activity.service';
import { AuthService } from '../../../core/services/auth.service';
import { User } from '../../../core/models/user.model';
import { Activity } from '../../../core/models/activity.model';
import { ApiResponse } from '../../../core/models/api-response.model';

@Component({
  selector: 'app-student-dashboard',
  standalone: true,
  imports: [CommonModule, RouterLink, NavbarComponent],
  templateUrl: './student-dashboard.component.html',
  styleUrls: ['./student-dashboard.component.scss']
})
export class StudentDashboardComponent implements OnInit, AfterViewInit {
  @ViewChild('doughnutChart') doughnutChartCanvas!: ElementRef<HTMLCanvasElement>;
  @ViewChild('barChart') barChartCanvas!: ElementRef<HTMLCanvasElement>;
  
  currentUser: User | null = null;
  activities: Activity[] = [];
  isLoading = true;
  errorMessage = '';
  totalPoints = 0;
  pendingCount = 0;
  approvedCount = 0;
  rejectedCount = 0;
  pointsBySemester: Record<number, number> = {};
  pointsByType: Record<string, number> = {
    sports: 0,
    mooc: 0,
    workshops: 0,
    internships: 0
  };
  
  doughnutChart: Chart | null = null;
  barChart: Chart | null = null;

  constructor(
    private activityService: ActivityService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.currentUser = this.authService.getCurrentUser();
    if (!this.currentUser) {
      this.router.navigate(['/auth/login']);
      return;
    }
    this.loadActivities();
  }

  ngAfterViewInit(): void {
    // Initialize charts after view is ready
    setTimeout(() => {
      this.initializeCharts();
    }, 0);
  }

  private loadActivities(): void {
    this.isLoading = true;
    this.errorMessage = '';
    
    this.activityService.getMyActivities().subscribe({
      next: (response: ApiResponse<Activity[]>) => {
        if (response.success && response.data) {
          this.activities = response.data;
          this.calculateStats();
        } else {
          this.errorMessage = response.message || 'Failed to load activities';
        }
        this.isLoading = false;
      },
      error: (error) => {
        console.error('Error loading activities:', error);
        this.errorMessage = 'Failed to load activities. Please try again later.';
        this.isLoading = false;
      }
    });
  }

  private calculateStats(): void {
    // Reset counters
    this.totalPoints = 0;
    this.pendingCount = 0;
    this.approvedCount = 0;
    this.rejectedCount = 0;
    this.pointsBySemester = {};
    this.pointsByType = {
      sports: 0,
      mooc: 0,
      workshops: 0,
      internships: 0
    };

    // Calculate statistics
    this.activities.forEach(activity => {
      // Count by status
      switch (activity.status) {
        case 'pending':
          this.pendingCount++;
          break;
        case 'approved':
          this.approvedCount++;
          this.totalPoints += activity.points;
          break;
        case 'rejected':
          this.rejectedCount++;
          break;
      }

      // Calculate points by semester
      if (activity.status === 'approved') {
        this.pointsBySemester[activity.semester] = (this.pointsBySemester[activity.semester] || 0) + activity.points;
      }

      // Calculate points by type
      if (activity.status === 'approved') {
        this.pointsByType[activity.activityType] = (this.pointsByType[activity.activityType] || 0) + activity.points;
      }
    });

    console.log('Points by semester:', this.pointsBySemester);
    console.log('Points by type:', this.pointsByType);
  }

  private initializeCharts(): void {
    if (!this.doughnutChartCanvas || !this.barChartCanvas) {
      console.error('Chart canvases not found');
      return;
    }

    // Initialize Doughnut Chart
    const doughnutCtx = this.doughnutChartCanvas.nativeElement.getContext('2d');
    if (!doughnutCtx) {
      console.error('Could not get doughnut chart context');
      return;
    }

    const doughnutConfig: ChartConfiguration = {
      type: 'doughnut',
      data: {
        labels: ['Pending', 'Approved', 'Rejected'],
        datasets: [{
          data: [this.pendingCount, this.approvedCount, this.rejectedCount],
          backgroundColor: ['#FFA500', '#4CAF50', '#F44336'],
          borderWidth: 1
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: true,
        layout: {
          padding: 10
        },
        plugins: {
          legend: {
            position: 'bottom',
            labels: {
              padding: 15,
              font: {
                size: 11
              },
              boxWidth: 12
            }
          }
        }
      }
    };

    this.doughnutChart = new Chart(doughnutCtx, doughnutConfig);

    // Initialize Bar Chart
    const barCtx = this.barChartCanvas.nativeElement.getContext('2d');
    if (!barCtx) {
      console.error('Could not get bar chart context');
      return;
    }

    const semesters = Object.keys(this.pointsBySemester).map(Number).sort((a, b) => a - b);
    const points = semesters.map(sem => this.pointsBySemester[sem]);

    const barConfig: ChartConfiguration = {
      type: 'bar',
      data: {
        labels: semesters.map(sem => `Semester ${sem}`),
        datasets: [{
          label: 'Points',
          data: points,
          backgroundColor: '#4CAF50',
          borderColor: '#388E3C',
          borderWidth: 1
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: true,
        scales: {
          y: {
            beginAtZero: true,
            title: {
              display: true,
              text: 'Points'
            }
          },
          x: {
            title: {
              display: true,
              text: 'Semester'
            }
          }
        },
        plugins: {
          legend: {
            display: false
          }
        }
      }
    };

    this.barChart = new Chart(barCtx, barConfig);
  }

  ngOnDestroy(): void {
    if (this.doughnutChart) {
      this.doughnutChart.destroy();
    }
    if (this.barChart) {
      this.barChart.destroy();
    }
  }
}
